    static boolean find = false;
    public static boolean exist(char[][] board, String word) {
    	for(int i=0;i<board.length;i++)
    		for(int j=0;j<board[0].length;j++)
    			if(!find && board[i][j] == word.charAt(0))
    				search(board, word, 0, i, j, "");
        return find;
    }
    
    public static void search(char[][] board, String word, int p, int i, int j, String visited) {
    	System.out.println("Searching "+i+","+j+ " for " + word.charAt(p));
    	if(find) return;
        if(p == word.length()-1 && word.charAt(p) == board[i][j]) 
        	find = true;
        if(word.charAt(p) == board[i][j]) {
            if(i-1>=0 && !visited.contains((i-1)+","+j))
                search(board, word, p+1, i-1, j, visited + "#" + (i-1) + "," + j);
            if(i+1<board.length && !visited.contains((i+1)+","+j))
                search(board, word, p+1, i+1, j, visited + "#" + (i+1) + "," + j);
            if(j-1>=0 && !visited.contains(i+","+(j-1)))
                search(board, word, p+1, i, j-1, visited + "#" + i + "," + (j-1));
            if(j+1<board[0].length && !visited.contains(i+","+(j+1)))
                search(board, word, p+1, i, j+1, visited + "#" + i + "," + (j+1));
        }
    }
	
